var _frontmatter = {
  "title": "Lidimist Manifesto",
  "release": "April 01, 2021",
  "version": "v1.1",
  "update": "Updated on April 10, 2021",
  "read": "15 min read",
  "author": "Rodrigo Favarete",
  "authorLink": "https://favarete.art/"
};
var layoutProps = {
  _frontmatter: _frontmatter
